            console.log(res);
            if (res != null) {
                this.matsnackbar.open("Email already exists", "Dismiss");
            }
            else {
                this.myservice.signup(this.RegEmail, this.RegPassword, this.RegFirstname, this.RegLastname, this.RegDob, this.RegRole).subscribe(res => {
